/*
Auto-generated by: https://github.com/pmndrs/gltfjsx
Command: npx gltfjsx@6.2.14 public/models/test.glb 
*/

import React, {useEffect, useRef} from 'react'
import {useGLTF, useAnimations} from '@react-three/drei'

export function Test(props) {
    const group = useRef()
    const {nodes, materials, animations} = useGLTF('models/test.glb')
    const {actions} = useAnimations(animations, group)

    console.log(actions)

    //comportement
    const playAction = () => {
        alert("playAction")
        actions['box-action'].repetitions = 1
        console.log(actions['box-action'].play().reset())
    }

    return (
        <group ref={group} {...props} dispose={null} onClick={playAction}>
            <group name="Scene">
                <group name="Armature" position={[0, 1.277, 0]}>
                    <primitive object={nodes['bone-cylinder']}/>
                </group>
                <group name="action1" position={[0, 1.268, 0]} rotation={[0, 0, -Math.PI]}>
                    <primitive object={nodes['bone-box']}/>
                </group>
            </group>
        </group>
    )
}

useGLTF.preload('models/test.glb')
