/*
Auto-generated by: https://github.com/pmndrs/gltfjsx
Command: npx gltfjsx@6.2.14 65548b0cf1a79ed2ebc0a60a.glb
*/

import React, {useEffect, useRef} from 'react'
import {useAnimations, useFBX, useGLTF} from '@react-three/drei'
import {useFrame} from "@react-three/fiber";

export function Avatar(props) {
    const avatar = useRef()
    const {nodes, materials} = useGLTF('models/65548b0cf1a79ed2ebc0a60a.glb')
    const {animations: standingAnimation} = useFBX('animations/Standing.fbx')
    const {animations: waving} = useFBX('animations/Waving.fbx')
    standingAnimation[0].name = "standing"
    waving[0].name = "waving"
    const {actions} = useAnimations([standingAnimation[0], waving[0]], avatar)
    useEffect(() => {
        actions["waving"].reset().fadeOut(0.6).stop()
        return () => {
            actions["standing"].reset().fadeIn(0.6).play()
        }
    }, [standingAnimation[0]])

    useFrame(state => {
        avatar.current.getObjectByName("Head").lookAt(state.camera.position)
    })
    return (
        <group rotation-x={-Math.PI/2} position-y={-6}>
            <group {...props} ref={avatar} dispose={null} scale={4.8}>
                <primitive object={nodes.Hips}/>
                <skinnedMesh morphTargetInfluences={[0]} geometry={nodes.Wolf3D_Hair.geometry} material={materials.Wolf3D_Hair}
                             skeleton={nodes.Wolf3D_Hair.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} name="EyeLeft" geometry={nodes.EyeLeft.geometry} material={materials.Wolf3D_Eye}
                             skeleton={nodes.EyeLeft.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} name="EyeRight" geometry={nodes.EyeRight.geometry} material={materials.Wolf3D_Eye}
                             skeleton={nodes.EyeRight.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} name="Wolf3D_Head" geometry={nodes.Wolf3D_Head.geometry} material={materials.Wolf3D_Skin}
                             skeleton={nodes.Wolf3D_Head.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} name="Wolf3D_Teeth" geometry={nodes.Wolf3D_Teeth.geometry} material={materials.Wolf3D_Teeth}
                             skeleton={nodes.Wolf3D_Teeth.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} geometry={nodes.Wolf3D_Outfit_Top.geometry} material={materials.Wolf3D_Outfit_Top}
                             skeleton={nodes.Wolf3D_Outfit_Top.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} geometry={nodes.Wolf3D_Outfit_Bottom.geometry} material={materials.Wolf3D_Outfit_Bottom}
                             skeleton={nodes.Wolf3D_Outfit_Bottom.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} geometry={nodes.Wolf3D_Outfit_Footwear.geometry} material={materials.Wolf3D_Outfit_Footwear}
                             skeleton={nodes.Wolf3D_Outfit_Footwear.skeleton}/>
                <skinnedMesh morphTargetInfluences={[0]} geometry={nodes.Wolf3D_Body.geometry} material={materials.Wolf3D_Body}
                             skeleton={nodes.Wolf3D_Body.skeleton}/>
            </group>
        </group>
    )
}

