import Fruit from "../components/Fruit";
import FruitForm from "../components/FruitForm";
import Btn from "../components/Btn";
import {useState} from "react";
import {Canvas} from "@react-three/fiber";
import {Box, OrbitControls} from "@react-three/drei";
import Cube from "../components/Cube";

export default function Home({fruits, setFruits}) {
    //state
    const [scale, setScale] = useState(false)

    //comportements

    const handleDelete = (id) => {
        const fruitsCopy = [...fruits]
        const fruitsCopyUpdated = fruitsCopy.filter(fruit => fruit.id !== id)
        setFruits(fruitsCopyUpdated)
    }

    const handleAdd = (ajouterFruit) => {
        const fruitsCopy = [...fruits]
        fruitsCopy.push(ajouterFruit)
        setFruits(fruitsCopy)
    }
    const scaled = () => {
        if (scale) {
            [2,2,2]
        }
    }


    return <div className={"Home"}>
        <div id={"presentation"} className="px-5 mb-5 d-flex">
            <div className="col-lg-8">
                <h1 className="titre mb-4">Bonjour, je suis Rafanekena</h1>
                <h3 className="colorWhite">Dev frontend | Graphic Designer</h3>
                <p className="col-10 colorWhite mb-5">
                    Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed
                    diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim
                    ad minim veniam, quis nostrud exerci.
                </p>
                <Btn/>
            </div>
            <div className="col-lg h-100">
                <Canvas>
                    <directionalLight position={[5,2,1]}/>
                    <Box rotation={[.4,.5,0]} scale={scaled} onDoubleClick={() => setScale(true)} />
                </Canvas>
            </div>
        </div>

        <div className={"fruits container-fluid d-flex px-5 py-3"}>
            <div className="col-4">
                <h3 className="colorWhite">Vos fruits :</h3>
                <ul className="">
                    {fruits.map((fruit) => <Fruit key={fruit.id} fruitsInfo={fruit} onClick={handleDelete}/>)}
                </ul>
                <FruitForm handleAdd={handleAdd}/>
            </div>
            <div className="col-8">
                <h3 className="colorWhite">Title Lorem ipsum</h3>
                <p className="colorWhite">
                    laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci
                    tation
                    ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo consequat. Duis autem vel eum iriure
                    dolor
                    in hendrerit in vulputate velit esse molestie consequat. Velit esse molestie consequat
                </p>
            </div>
        </div>
    </div>
};
