import Mac from "../img/macbook.png"
import WebDesign from "../img/presentation-web-design.png";
import {useState} from "react";
import BoxProject from "../components/portfolio/BoxProject";

export default function Portfolio() {
    //state
    const [detail, setDetail] = useState("portfolio")

    //comportement
    console.log(detail)

    const allProject = <>
        <h1 className="textCenter colorGreen">Portfolio</h1>
        <div className="row my-5 PFContent">
            <BoxProject caption={WebDesign} setDetail={setDetail} nom={"projet1"}/>
            <BoxProject caption={Mac} setDetail={setDetail} nom={"projet2"}/>
            <BoxProject setDetail={setDetail} nom={"projet3"}/>
            <div className="col-lg-4 mb-4" onClick={()=> setDetail("projet4")}>
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308,
                    cursor: "pointer"
                }}/>
            </div>
            <div className="col-lg-4 mb-4" onClick={()=> setDetail("projet5")}>
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308,
                    cursor: "pointer"
                }}/>
            </div>
            <div className="col-lg-4 mb-4" onClick={()=> setDetail("projet6")}>
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308,
                    cursor: "pointer"
                }}/>
            </div>
        </div>
    </>

    return (
        <div className="portfolio">
            {
                detail==="portfolio" ? allProject
                : detail==="projet1" ? (<h1 className={detail==="projet1" ? "fadeIn" : ""} onClick={()=>setDetail("portfolio")}>Projet #1</h1>)
                : detail==="projet2" ? (<h1 onClick={()=>setDetail("portfolio")}>Projet #2</h1>)
                : detail==="projet3" ? (<h1 onClick={()=>setDetail("portfolio")}>Projet #3</h1>)
                : detail==="projet4" ? (<h1 onClick={()=>setDetail("portfolio")}>Projet #4</h1>)
                : detail==="projet5" ? (<h1 onClick={()=>setDetail("portfolio")}>Projet #5</h1>)
                : detail==="projet6" ? (<h1 onClick={()=>setDetail("portfolio")}>Projet #6</h1>)
                : null
            }
        </div>
    )
}
