import WebDesign from "../img/presentation-web-design.png"
import Mac from "../img/macbook.png"
import {useState} from "react";

export default function Portfolio() {
    //state
    const [detail, setDetail] = useState("portfolio")
    const data = [
        "banane",
        "coco",
        "cerise"
    ]

    //comportement
    const handleDetail1 = () => {
        alert("handleDetail")
    }

    const allProject = <>
        <h1 className="textCenter colorGreen">Portfolio</h1>
        <ul>
            {data.map((item) => (
                <li key={item}>{item}</li>
            ))}
        </ul>
        <div className="row my-5 PFContent">
            <div className="col-lg-4 mb-4" onClick={handleDetail1}>
                <div style={{
                    borderRadius: 18,
                    height: 308,
                    overflow: "hidden"
                }}>
                    <img src={WebDesign} alt="#" width="185%"/>
                </div>
            </div>
            <div className="col-lg-4 mb-4">
                <div style={{
                    borderRadius: 18,
                    height: 308,
                    overflow: "hidden"
                }}>
                    <img src={Mac} alt="#" width="135%"/>
                </div>
            </div>
            <div className="col-lg-4 mb-4">
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308}}/>
            </div>
            <div className="col-lg-4 mb-4">
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308}}/>
            </div>
            <div className="col-lg-4 mb-4">
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308}}/>
            </div>
            <div className="col-lg-4 mb-4">
                <div style={{
                    border: "1px dashed white",
                    borderRadius: 18,
                    height: 308}}/>
            </div>
        </div>
    </>

    return (
        <div className="portfolio">
            {detail==="portfolio" ? allProject : null}
        </div>
    )
}
