import WebDesign from "../img/presentation-web-design.png";
import TmplPub from "../img/tmpl-pub.jpg"

import {useState} from "react";
import BoxProject from "../components/portfolio/BoxProject";
import DetailProject from "../components/portfolio/DetailProject";
import {render} from "@react-three/fiber";

export default function Portfolio() {
    //state
    const [detail, setDetail] = useState("portfolio")

    //comportement
    console.log(detail)

    const allProject = <>
        <h1 className="textCenter colorGreen">Portfolio</h1>
        <div className="row my-5 PFContent">
            <BoxProject caption={WebDesign} altImg="web design" setDetail={setDetail} nom={"projet1"}/>
            <BoxProject caption={TmplPub} altImg="macbook" setDetail={setDetail} nom={"projet2"}/>
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet3"}/>
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet4"} />
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet5"} />
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet6"} />
        </div>
    </>

    return (
        <div className="portfolio">
            {
                /*detail==="portfolio" ? allProject
                : detail==="projet1" ? (
                        <DetailProject detail={detail} sousTitre="Web design" image1={WebDesign} setDetail={setDetail} titre="Projet #1" />)
                : detail==="projet2" ? (
                        <DetailProject detail={detail} image1={TmplPub} setDetail={setDetail} titre="Projet #2"/>)
                : detail==="projet3" ? (
                        <DetailProject detail={detail} setDetail={setDetail} titre="Projet #3"/>)
                : detail==="projet4" ? (
                        <DetailProject detail={detail} setDetail={setDetail} titre="Projet #4"/>)
                : detail==="projet5" ? (
                        <DetailProject detail={detail} setDetail={setDetail} titre="Projet #5"/>)
                : detail==="projet6" ? (
                        <DetailProject detail={detail} setDetail={setDetail} titre="Projet #6"/>)
                : null*/
                () => {
                    if (detail==="portfolio") {
                        render(allProject)
                    }
                }
            }
        </div>
    )
}
