import {PerspectiveCamera, RenderTexture, Text, MeshDistortMaterial} from "@react-three/drei";
import {useRef} from "react";
import {useFrame} from "@react-three/fiber";

export default function Cube() {
    //state
    const textRef = useRef()
    useFrame(state => (textRef.current.position.x = Math.sin(state.clock.elapsedTime)))

    return (
        <mesh rotation={[0,0,0]}>
            {/*<boxGeometry args={[1.6,1.6,1.6]}/>*/}
            <sphereGeometry args={[2,200,200]}/>
            <MeshDistortMaterial distort={.3} speed={1}>
                <RenderTexture attach="map">
                    <PerspectiveCamera makeDefault position={[5,0,33]}/>
                    <color attach="background" args={["gray"]}/>
                    <Text ref={textRef} fontSize={4} color="white">
                        Bonjour
                    </Text>
                </RenderTexture>
            </MeshDistortMaterial>
            {/*<RenderTexture attach="map">
                <PerspectiveCamera makeDefault position={[0,0,2]}/>
                <color attach="background" args={["#e76666"]}/>
