import "./styles.css";
import {useState} from "react";


export default function App() {
    const [compteur, setCompteur] = useState(0)

    const handleIncrement = () => {
        setCompteur(compteur + 1)
    }
    const handleDecrement = () => {
        setCompteur(compteur - 1)
    }
  return (
    <div className="App">
      <h1 className="titre">Bienvenu dans la porté de l'univers</h1>
      <h2>Nombre {compteur}</h2>

        <div className="container">
            <div className="btn" onClick={handleDecrement}>Décrémente -</div>
            <div className="btn" onClick={handleIncrement}>Incrémente +</div>
        </div>
    </div>
  );
}
