import WebDesign from "../img/presentation-web-design.png";
import TmplPub from "../img/tmpl-pub.jpg"
import CarteVisite from "../img/business-card.jpg"

import {useState} from "react";
import BoxProject from "../components/portfolio/BoxProject";
import DetailProject from "../components/portfolio/DetailProject";

export default function Portfolio() {
    //state
    const [detail, setDetail] = useState("portfolio")

    //comportement
    console.log(detail)

    const allProject = <>
        <h1 className="textCenter colorGreen">Portfolio</h1>
        <div className="row my-5 PFContent">
            <BoxProject caption={WebDesign} altImg="web design" titre="Web design" setDetail={setDetail} nom={"projet1"}/>
            <BoxProject caption={TmplPub} altImg="macbook" titre="Template" setDetail={setDetail} nom={"projet2"}/>
            <BoxProject caption={CarteVisite} altImg="carte de visite" titre="Template" setDetail={setDetail} nom={"projet3"}/>
            <BoxProject altImg="pas d'image" titre="Template" setDetail={setDetail} nom={"projet4"} />
            <BoxProject altImg="pas d'image" titre="Template" setDetail={setDetail} nom={"projet5"} />
            <BoxProject altImg="pas d'image" titre="Template" setDetail={setDetail} nom={"projet6"} />
        </div>
    </>

    return (
        <div className="portfolio">
            {
                detail==="portfolio" ? allProject
                : detail==="projet1" ? (
                        <DetailProject
                            detail={detail}
                            image1={WebDesign}
                            setDetail={setDetail}
                            titre="Projet #1"
                            sousTitre="Web design"
                            textContent1="
                            Lorem ipsum dolor sit amet. Mozar da vinci et trove glames, sit amet ipsum dolor
                            "
                            textContent2="
                            Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod
                            tincidunt ut laoreet dolore magna aliquam erat volutpat."
                        />)
                : detail==="projet2" ? (
                        <DetailProject
                            detail={detail}
                            image1={TmplPub}
                            setDetail={setDetail}
                            titre="Projet #2"
                            sousTitre="Template cover"
                            textContent1="
                            Lorem ipsum dolor sit amet. Mozar da vinci et trove glames, sit amet ipsum dolor
                            "
                            textContent2="
                            Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy
                            nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. "
                        />)
                : detail==="projet3" ? (
                        <DetailProject
                            detail={detail}
                            image1={CarteVisite}
                            setDetail={setDetail}
                            titre="Projet #3"
                            sousTitre="Carte de visite"
                            textContent1="
                            Lorem ipsum dolor sit amet. Mozar da vinci et trove glames, sit amet ipsum dolor
                            "
                            textContent2="
                            Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy
                            nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. "
                        />)
                : detail==="projet4" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #4"
                        />)
                : detail==="projet5" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #5"
                        />)
                : detail==="projet6" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #6"
                        />)
                : null
            }
        </div>
    )
}
