import {useFrame} from "@react-three/fiber";
import {useRef} from "react";
import Img from "../img/Groupe 15@2x.png"
import {
    Image,
    PerspectiveCamera,
    RenderTexture,
    Text
} from "@react-three/drei";

export default function Cube() {

    const boxRef = useRef()
    useFrame(state => (boxRef.current.rotation.x += 0.01))
    useFrame(state => (boxRef.current.rotation.z += 0.005))

    return (
        <mesh rotation={[0,0,0]} ref={boxRef}>
            <boxGeometry args={[3.6,3.6,3.6]}/>
            <meshPhongMaterial>
                <PerspectiveCamera position={[0,0,3]}/>
                <RenderTexture attach="map">
                    <Image url={Img} scale={9} position={[0,0,4]}/>
                    {/*<color attach="background" args={["black"]}/>*/}
                    {/*<Text ref={textRef} color="#D6F42A" fontSize={4}>
                        Services
                    </Text>*/}
                </RenderTexture>
            </meshPhongMaterial>
        </mesh>
    )
}
