import {useFrame} from "@react-three/fiber";
import {useRef} from "react";
import Img from "../img/Groupe 15@2x.png"
import {
    Image,
    PerspectiveCamera,
    RenderTexture,
    Text
} from "@react-three/drei";

export default function Cube() {

    const boxRef = useRef()
    useFrame(state => (boxRef.current.rotation.y += 0.006))

    return (
        <mesh rotation={[0.2,1,3]} ref={boxRef}>
            <cylinderGeometry args={[3,3,0.2]}/>
            <meshPhongMaterial>
                <PerspectiveCamera position={[0,0,3]}/>
                <RenderTexture attach="map" image={Img}>
                    <Image url={Img} scale={9}/>
                    {/*<color attach="background" args={["black"]}/>*/}
                    {/*<Text ref={textRef} color="#D6F42A" fontSize={4}>
                        Services
                    </Text>*/}
                </RenderTexture>
            </meshPhongMaterial>
        </mesh>
    )
}
