import Fruit from "../components/Fruit";
import FruitForm from "../components/FruitForm";
import {useState} from "react";

export default function Home({fruits, setFruits}) {
    //state

    //comportements
    const handleDelete = (id) => {
        const fruitsCopy = [...fruits]
        const fruitsCopyUpdated = fruitsCopy.filter(fruit => fruit.id !== id)
        setFruits(fruitsCopyUpdated)
    }

    const handleAdd = (ajouterFruit) => {
        const fruitsCopy = [...fruits]
        fruitsCopy.push(ajouterFruit)
        setFruits(fruitsCopy)
    }

    return (
        <div className="">
            <div className="px-5">
                <div className="col-lg-8">
                    <h1 className="titre mb-5">Bonjour,</h1>
                    <h3 className="colorWhite">Bienvenue au bon marcher des fruits.</h3>
                    <p className="col-10 colorWhite mb-5">Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed
                        diam nonummy nibh euismod tincidunt ut
                        laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci.
                    </p>
                </div>
                <div className="col-lg">
                    <img src="../img/048-v2.jpg" alt=""/>
                </div>

            </div>

            <div className="fruits container-fluid d-flex px-5 py-3">
                <div className="col-4">
                    <h3 className="colorWhite">Vos fruits :</h3>
                    <ul className="">
                        {fruits.map((fruit) => (
                            /*<li key={fruit.id}>
                                {fruit.nom} <button onClick={() => handleDelete(fruit.id)}>X</button>
                            </li>*/
                            <Fruit key={fruit.id} fruitsInfo={fruit} onClick={handleDelete}/>
                        ))}
                    </ul>
                    <FruitForm handleAdd={handleAdd}/>
                </div>
                <div className="col-8">
                    <h3 className="colorWhite">Title Lorem ipsum</h3>
                    <p className="colorWhite">
                        laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci
                        tation
                        ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo consequat. Duis autem vel eum iriure
                        dolor
                        in hendrerit in vulputate velit esse molestie consequat. Velit esse molestie consequat
                    </p>
                </div>
            </div>
        </div>
    )
};
