import {useFrame} from "@react-three/fiber";
import {useRef} from "react";
import {
    PerspectiveCamera,
    RenderTexture,
    Text
} from "@react-three/drei";

export default function Cube() {
    const textRef = useRef()
    const boxRef =useRef()
    useFrame(state => (textRef.current.position.x = Math.sin(state.clock.elapsedTime)*4))
    useFrame(state => (boxRef.current.rotation.x = Math.sin(state.clock.elapsedTime)))

    return (
        <mesh rotation={[1,0.5,-0.2]} ref={boxRef}>
            <boxGeometry args={[3,3,3]}/>
            <meshPhongMaterial>
                <PerspectiveCamera position={[0,0,3]}/>
                <RenderTexture attach="map">
                    <color attach="background" args={["#333333"]}/>
                    <Text ref={textRef} color="#D6F42A" fontSize={4}>
                        Services
                    </Text>
                </RenderTexture>
            </meshPhongMaterial>
        </mesh>
    )
}
