import Mac from "../img/macbook.png"
import WebDesign from "../img/presentation-web-design.png";
import {useState} from "react";
import BoxProject from "../components/portfolio/BoxProject";
import DetailProject from "../components/portfolio/DetailProject";

export default function Portfolio() {
    //state
    const [detail, setDetail] = useState("portfolio")

    //comportement
    console.log(detail)

    const allProject = <>
        <h1 className="textCenter colorGreen">Portfolio</h1>
        <div className="row my-5 PFContent">
            <BoxProject caption={WebDesign} altImg="web design" setDetail={setDetail} nom={"projet1"}/>
            <BoxProject caption={Mac} altImg="macbook" setDetail={setDetail} nom={"projet2"}/>
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet3"}/>
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet4"} />
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet5"} />
            <BoxProject altImg="pas d'image" setDetail={setDetail} nom={"projet6"} />
        </div>
    </>

    return (
        <div className="portfolio">
            {
                detail==="portfolio" ? allProject
                : detail==="projet1" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #1" />
                    )
                : detail==="projet2" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #2"/>
                    )
                : detail==="projet3" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #3"/>
                    )
                : detail==="projet4" ? (
                        <DetailProject
                            detail={detail}
                            setDetail={setDetail}
                            titre="Projet #4"/>
                    )
                : detail==="projet5" ? (
                    <h1 onClick={()=>setDetail("portfolio")}>Projet #5</h1>)
                : detail==="projet6" ? (<h1 onClick={()=>setDetail("portfolio")}>Projet #6</h1>)
                : null
            }
        </div>
    )
}
