import "./styles.css";
import {useState} from "react";
import Fruit from "./components/Fruit";

export default function App() {
    //state
    const [fruits, setFruits] = useState([
        {id: 1, nom: "Pamplemousse"},
        {id: 2, nom: "Banane"},
        {id: 3, nom: "Cerise"},
    ])

    const [nouveauFruit, setNouveauFruit] = useState("")

    //comportements
    const handleDelete = (id) => {
        const fruitsCopy = [...fruits]
        const fruitsCopyUpdated = fruitsCopy.filter(fruit => fruit.id !== id)
        setFruits(fruitsCopyUpdated)
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        const fruitsCopy = [...fruits]
        if (nouveauFruit !== "") {
            const id = new Date().getTime()
            const nom = nouveauFruit
            fruitsCopy.push({id, nom})
            setFruits(fruitsCopy)
            setNouveauFruit("")
        }
    }

    const handleChange = (event) => {
        setNouveauFruit(event.target.value)
    }

    //affichage
    return (
        <div className="App">
            <h1 className="titre">Bienvenu dans la port√© de l'univers</h1>
            <h2>Listes des fruits :</h2>
            <ul>
                {/*{fruits.map((fruit) => (
                    <li key={fruit.id}>
                        {fruit.nom} <button onClick={() => handleDelete(fruit.id)}>X</button>
                    </li>
                ))}*/}
                <Fruit/>
            </ul>
            <form action="submit" onSubmit={handleSubmit}>
                <input type="text" value={nouveauFruit} onChange={handleChange} placeholder="Ajouter un fruit"/>
                <button type="submit">Ajouter +</button>
            </form>
        </div>
    );
}
