import Fruit from "../components/Fruit";
import FruitForm from "../components/FruitForm";
import {useState} from "react";

export default function Home() {
    //state
    const [fruits, setFruits] = useState([
        {id: 1, nom: "Pamplemousse"},
        {id: 2, nom: "Banane"},
        {id: 3, nom: "Cerise"},
    ])

    //comportements
    const handleDelete = (id) => {
        const fruitsCopy = [...fruits]
        const fruitsCopyUpdated = fruitsCopy.filter(fruit => fruit.id !== id)
        setFruits(fruitsCopyUpdated)
    }

    const handleAdd = (ajouterFruit) => {
        const fruitsCopy = [...fruits]
        fruitsCopy.push(ajouterFruit)
        setFruits(fruitsCopy)
    }


    return (
        <div>
            <h1 className="titre">Bienvenu dans la port√© de l'univers</h1>
            <h2>Listes des fruits :</h2>
            <ul>
                {fruits.map((fruit) => (
                    /*<li key={fruit.id}>
                        {fruit.nom} <button onClick={() => handleDelete(fruit.id)}>X</button>
                    </li>*/
                    <Fruit key={fruit.id} fruitsInfo={fruit} onClick={handleDelete} />
                ))}

            </ul>
            <FruitForm handleAdd={handleAdd}/>
        </div>
    )
};
