import Btn from "../components/Btn";
import {useState} from "react";
import {OrbitControls} from "@react-three/drei";
import {Canvas} from "@react-three/fiber";
import Salutation from "../components/Salutation";
export default function Home({fruits, setFruits}) {
    //state

    //comportements

    const handleDelete = (id) => {
        const fruitsCopy = [...fruits]
        const fruitsCopyUpdated = fruitsCopy.filter(fruit => fruit.id !== id)
        setFruits(fruitsCopyUpdated)
    }

    const handleAdd = (ajouterFruit) => {
        const fruitsCopy = [...fruits]
        fruitsCopy.push(ajouterFruit)
        setFruits(fruitsCopy)
    }

    return <div className={"Home"}>
        <div id={"presentation"} className="mb-5 d-flex">
            <div className="col-lg-8">
                <div style={{zIndex: 10}}>
                    <Salutation/>
                    <div className="nom"><h1>Ra Fanekena</h1></div>
                    <h3 className="colorWhite">Dev frontend | Graphic Designer</h3>
                    <p className="col-10 colorWhite mb-5">
                        Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed
                        diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim
                        ad minim veniam, quis nostrud exerci.
                    </p>
                    <Btn/>
                </div>
            </div>
            <div className="col-lg h-100">

            </div>
        </div>
    </div>
};
