import {useFrame} from "@react-three/fiber";
import {useRef} from "react";
import Img from "../img/Groupe 15@2x.png"
import {
    Image,
    PerspectiveCamera,
    RenderTexture,
    Text
} from "@react-three/drei";

export default function Cube() {
    const textRef = useRef()
    useFrame(state => (textRef.current.position.x = Math.sin(state.clock.elapsedTime)*3))

    return (
        <mesh rotation={[0.2,0.6,1]}>
            <boxGeometry args={[3.6,3.6,3.6]}/>
            <meshPhongMaterial>
                <PerspectiveCamera position={[0,0,3]}/>
                <RenderTexture attach="map">
                    {/*<Image url={Img} scale={9}/>*/}
                    <color attach="background" args={["gray"]}/>
                    <Text ref={textRef} color="#D6F42A" fontSize={4}>
                        Services
                    </Text>
                </RenderTexture>
            </meshPhongMaterial>
        </mesh>
    )
}
